
SET NAMES utf8;
SET time_zone = '+00:00';
SET foreign_key_checks = 0;
SET sql_mode = 'NO_AUTO_VALUE_ON_ZERO';

SET NAMES utf8mb4;

DELIMITER //

CREATE TRIGGER quotes_before_insert
BEFORE INSERT ON quotes
FOR EACH ROW
BEGIN
    SET NEW.hash = MD5(NEW.text);
END;
//

CREATE TRIGGER quotes_before_update
BEFORE UPDATE ON quotes
FOR EACH ROW
BEGIN
    SET NEW.hash = MD5(NEW.text);
END;
//

DELIMITER ;

